MemoSon Component

const isEqual = (prevProps, nextProps) => {
  if (prevProps.count2 !== nextProps.count2) {
    return false;
  }
  return true;
};

export default memo((props = {}) => {
  console.log(`--- memo re-render ---`);
  return count2: {props.count2};
