webpack.config.js
module.exports = {
  mode: "development",
  entry: './src/index.js'
  output: {
    filename: 'js/index.js',
    path: path.join(__dirname, './dist')
  },
  module: {
    rules: [
      {
        test: /\.(gif|png|jpg)$/, // 匹配gif
        use: [
          // 第三方处理插件
          {
            loader: 'url-loader',
            options: {
              limit: 8192, // 小雨8k
              name: '[name][hash:8].[ext]' // 文件名+8位hash值
            }
          },
        ],
      },
      {
        test: /\.(css|less)$/,
        // loader从右向左的顺序使用
        use: [
          'style-loader',
          'css-loader',
          'less-loader',
          'postcss-loader'
        ],
      }
    ]
  },
  plugins: [
    new MiniCssExtractPlugin({
      filename: '[name].css'
    })
  ]
};


